
/*
 * 		&
 * 		|
 * 		^
 */


public class 비트연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Integer.toBinaryString(35));
		System.out.println(Integer.toBinaryString(17));
		System.out.println(35 & 17);
		System.out.println(35 | 17);
		System.out.println(35 ^ 17);
		
		
		
		
		
	}

}
